import React, { useEffect, useState } from "react";
import axios from "axios";

function FavoriteCities({ favoriteCities }) {
  const [weatherData, setWeatherData] = useState([]);

  useEffect(() => {
    // Check if favoriteCities is defined and is an array with length > 0
    if (!favoriteCities || favoriteCities.length === 0) {
      setWeatherData([]);
      return;
    }

    const fetchAllWeather = async () => {
      try {
        const promises = favoriteCities.map((city) =>
          axios.get(`http://localhost:5000/api/weather?city=${city}`).then((res) => res.data)
        );
        const results = await Promise.all(promises);
        setWeatherData(results);
      } catch (error) {
        console.error("Failed to fetch favorite cities weather", error);
      }
    };
    fetchAllWeather();
  }, [favoriteCities]);

  if (!weatherData || weatherData.length === 0) return <p>No favorite cities added yet.</p>;

  return (
    <div
      className="favorites-grid"
      style={{
        display: "grid",
        gridTemplateColumns: "repeat(auto-fit, minmax(200px, 1fr))",
        gap: "16px",
      }}
    >
      {weatherData.map((data) => (
        <div
          key={data.id}
          className="favorite-card"
          style={{
            border: "1px solid #ccc",
            borderRadius: "8px",
            padding: "12px",
          }}
        >
          <h3 style={{ margin: "0 0 8px" }}>
            {data.name}, {data.sys.country}
          </h3>
          <p style={{ textTransform: "capitalize", margin: "4px 0" }}>
            {data.weather[0].description}
          </p>
          <p style={{ margin: "4px 0" }}>Temp: {Math.round(data.main.temp)}Â°C</p>
          <p style={{ margin: "4px 0" }}>Humidity: {data.main.humidity}%</p>
          <p style={{ margin: "4px 0" }}>Wind: {data.wind.speed} km/h</p>
        </div>
      ))}
    </div>
  );
}

export default FavoriteCities;
