import React from "react";

function WeatherCard({ data, isFavorite, onToggleFavorite }) {
  if (!data) return <p>No city selected or data unavailable.</p>;

  const handleFavoriteClick = () => {
    if (onToggleFavorite) onToggleFavorite(data);
  };

  return (
    <div
      className="weather-card"
      style={{
        border: "1px solid #ccc",
        borderRadius: "8px",
        padding: "16px",
        position: "relative",
        marginBottom: "20px",
      }}
    >
      {/* Heart emoji toggle */}
      <div
        onClick={handleFavoriteClick}
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          fontSize: "24px",
          cursor: "pointer",
          color: isFavorite ? "red" : "gray",
          userSelect: "none",
        }}
        title={isFavorite ? "Remove from favorites" : "Add to favorites"}
        role="button"
        tabIndex={0}
        onKeyPress={(e) => e.key === "Enter" && handleFavoriteClick()}
      >
        ❤️
      </div>

      <div className="city-name" style={{ fontSize: "24px", fontWeight: "bold" }}>
        {data.name}, {data.sys.country}
      </div>

      <div className="weather-info" style={{ marginTop: "10px" }}>
        <div className="weather-main" style={{ display: "flex", alignItems: "center" }}>
          <div className="temp" style={{ fontSize: "48px", marginRight: "16px" }}>
            {Math.round(data.main.temp)}°C
          </div>
          <div className="description" style={{ fontSize: "18px", textTransform: "capitalize" }}>
            {data.weather[0].description}
          </div>
        </div>

        <div className="weather-details" style={{ marginTop: "10px", fontSize: "16px" }}>
          <p>
            <strong>Humidity:</strong> {data.main.humidity}%
          </p>
          <p>
            <strong>Wind:</strong> {data.wind.speed} km/h
          </p>
          <p>
            <strong>Pressure:</strong> {data.main.pressure} hPa
          </p>
          <p>
            <strong>Visibility:</strong> {data.visibility / 1000} km
          </p>
        </div>
      </div>
    </div>
  );
}

export default WeatherCard;
