import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import axios from "axios";

import Navbar from "./components/Navbar";
import Sidebar from "./components/Sidebar";
import WeatherCard from "./components/WeatherCard";
import FavoriteCities from "./components/FavoriteCities";

function App() {
  const [weatherData, setWeatherData] = useState(null);
  const [searchCity, setSearchCity] = useState("");
  const [selectedCity, setSelectedCity] = useState("");

  // Fetch weather for selected city or user location
  useEffect(() => {
    if (selectedCity) {
      fetchWeather(selectedCity);
    } else {
      navigator.geolocation.getCurrentPosition(async (pos) => {
        const { latitude, longitude } = pos.coords;
        try {
          const response = await axios.get(
            `http://localhost:5000/api/weather/geo?lat=${latitude}&lon=${longitude}`
          );
          setWeatherData(response.data);
        } catch (err) {
          console.error("Error fetching geo weather", err);
        }
      });
    }
  }, [selectedCity]);

  const fetchWeather = async (city) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/weather?city=${city}`
      );
      setWeatherData(response.data);
    } catch (err) {
      console.error("Error fetching weather", err);
    }
  };

  const handleSearch = () => {
    if (searchCity.trim()) {
      setSelectedCity(searchCity);
      setSearchCity("");
    }
  };

  return (
    <Router>
      <div className="app">
        <nav>
          <Navbar />
        </nav>

        <div className="content-wrapper">
          <aside className="sidebar">
            <Sidebar />
          </aside>

          <main className="main-content">
            <div className="search-bar">
              <input
                type="text"
                placeholder="Search city..."
                value={searchCity}
                onChange={(e) => setSearchCity(e.target.value)}
              />
              <button onClick={handleSearch}>Search</button>
            </div>

            <Routes>
              <Route
                path="/"
                element={weatherData ? <WeatherCard data={weatherData} /> : <p>Loading...</p>}
              />
              <Route path="/favorites" element={<FavoriteCities />} />
            </Routes>
          </main>
        </div>
      </div>
    </Router>
  );
}

export default App;
