import React from "react";
import { useNavigate } from "react-router-dom";
import "./HeroPage.css"; // You can style it separately or inline

function HeroPage() {
  const navigate = useNavigate();

  const handleStart = () => {
    navigate("/");
  };

  return (
    <div className="hero-container">
      <div className="hero-content">
        <h1>üå¶Ô∏è Weather Dashboard</h1>
        <p>
          Welcome to the Weather Dashboard! This app allows you to:
        </p>
        <ul>
          <li>üîç Search weather conditions by city</li>
          <li>üìç View weather for your current location</li>
          <li>‚ù§Ô∏è Mark cities as favorites</li>
          <li>üìä See temperature, humidity, wind speed & more</li>
        </ul>
        <button className="hero-btn" onClick={handleStart}>
          Go to Dashboard ‚Üí
        </button>
      </div>
    </div>
  );
}

export default HeroPage;
