import React from "react";

function FavoriteCities({ favoriteCities = [], setFavoriteCities, setSelectedCity }) {
  const removeCity = (city) => {
    setFavoriteCities(favoriteCities.filter((c) => c !== city));
  };

  return (
    <div>
      <h2>Favorite Cities</h2>
      {favoriteCities.length === 0 && <p>No favorite cities yet.</p>}
      <ul>
        {favoriteCities.map((city) => (
          <li key={city} style={{ marginBottom: "8px" }}>
            <button
              style={{ cursor: "pointer", marginRight: "10px" }}
              onClick={() => setSelectedCity(city)}
            >
              {city}
            </button>
            <button onClick={() => removeCity(city)} aria-label={`Remove ${city} from favorites`}>
              âœ•
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default FavoriteCities;
