import React from "react";

function WeatherCard({ data, favoriteCities, setFavoriteCities }) {
  const city = data.name;

  // Check if city is favorite
  const isFavorite = favoriteCities.includes(city);

  // Toggle favorite city
  const toggleFavorite = () => {
    if (isFavorite) {
      setFavoriteCities(favoriteCities.filter((c) => c !== city));
    } else {
      setFavoriteCities([...favoriteCities, city]);
    }
  };

  return (
    <div
      style={{
        border: "1px solid #ccc",
        borderRadius: 8,
        padding: 20,
        marginBottom: 20,
        position: "relative",
      }}
    >
      <button
        onClick={toggleFavorite}
        style={{
          position: "absolute",
          top: 12,
          right: 12,
          background: "none",
          border: "none",
          cursor: "pointer",
          fontSize: 24,
          color: isFavorite ? "red" : "gray",
        }}
        aria-label={isFavorite ? "Remove from favorites" : "Add to favorites"}
      >
        ♥
      </button>

      <h3>
        {data.name}, {data.sys.country}
      </h3>
      <p style={{ textTransform: "capitalize" }}>{data.weather[0].description}</p>
      <p>Temp: {Math.round(data.main.temp)}°C</p>
      <p>Humidity: {data.main.humidity}%</p>
      <p>Wind: {data.wind.speed} km/h</p>
    </div>
  );
}

export default WeatherCard;
