import React from "react";

function WeatherCard({ data, favoriteCities, setFavoriteCities }) {
  if (!data) return null;

  const cityName = data.name;

  const toggleFavorite = () => {
    if (!setFavoriteCities) {
      console.error("setFavoriteCities is not a function");
      return;
    }
    if (favoriteCities.includes(cityName)) {
      setFavoriteCities(favoriteCities.filter((city) => city !== cityName));
    } else {
      setFavoriteCities([...favoriteCities, cityName]);
    }
  };

  const isFavorite = favoriteCities.includes(cityName);

  return (
    <div style={{ border: "1px solid #ccc", padding: "16px", borderRadius: "8px" }}>
      <h2>{cityName}, {data.sys?.country}</h2>
      <p>{data.weather[0]?.description}</p>
      <p>Temp: {Math.round(data.main?.temp)}¬∞C</p>
      <p>Humidity: {data.main?.humidity}%</p>
      <p>Wind: {data.wind?.speed} km/h</p>

      <button 
        onClick={toggleFavorite} 
        style={{ 
          fontSize: "24px", 
          color: isFavorite ? "red" : "gray", 
          background: "none", 
          border: "none", 
          cursor: "pointer" 
        }}
        aria-label={isFavorite ? "Remove from favorites" : "Add to favorites"}
      >
        {isFavorite ? "‚ù§Ô∏è" : "ü§ç"}
      </button>
    </div>
  );
}

export default WeatherCard;
