import React, { useState } from "react";

function WeatherCard({ data, isFavorite, onToggleFavorite }) {
  if (!data) return null;

  const handleFavoriteClick = () => {
    if (onToggleFavorite) {
      onToggleFavorite(data);
    }
  };

  return (
    <div className="weather-card">
      {/* Favorite heart toggle */}
      <div
        className={`favorite-heart ${isFavorite ? "favorited" : ""}`}
        onClick={handleFavoriteClick}
        title={isFavorite ? "Remove from favorites" : "Add to favorites"}
        role="button"
        tabIndex={0}
        onKeyPress={(e) => {
          if (e.key === "Enter") handleFavoriteClick();
        }}
      >
        ❤️
      </div>

      <div className="city-name">
        {data.name}, {data.sys.country}
      </div>
      <div className="weather-info">
        <div className="weather-main">
          <div className="temp">{Math.round(data.main.temp)}°C</div>
          <div className="description">{data.weather[0].description}</div>
        </div>
        <div className="weather-details">
          <p>
            <strong>Humidity:</strong> {data.main.humidity}%
          </p>
          <p>
            <strong>Wind:</strong> {data.wind.speed} km/h
          </p>
          <p>
            <strong>Pressure:</strong> {data.main.pressure} hPa
          </p>
          <p>
            <strong>Visibility:</strong> {data.visibility / 1000} km
          </p>
        </div>
      </div>
    </div>
  );
}

export default WeatherCard;
