import React from "react";

function WeatherCard({ data, favoriteCities = [], setFavoriteCities }) {
  const city = data.name;
  const isFavorite = favoriteCities.some((c) => c.name === city);

  const toggleFavorite = () => {
    if (isFavorite) {
      setFavoriteCities(favoriteCities.filter((c) => c.name !== city));
    } else {
      setFavoriteCities([...favoriteCities, data]);
    }
  };

  const getEmoji = () => {
    const hour = new Date().getHours();
    const weather = data.weather[0].main.toLowerCase();

    if (weather.includes("cloud")) return "‚õÖ";
    if (weather.includes("rain")) return "üåßÔ∏è";
    if (weather.includes("snow")) return "‚ùÑÔ∏è";
    if (hour >= 6 && hour < 18) return "‚òÄÔ∏è";
    return "üåô";
  };

  const getTimeOfDay = () => {
    const hour = new Date().getHours();

    if (hour >= 5 && hour < 12) return "Morning";
    else if (hour >= 12 && hour < 17) return "Afternoon";
    else if (hour >= 17 && hour < 20) return "Evening";
    else return "Night";
  };

  return (
    <div
      style={{
        border: "1px solid #ddd",
        padding: "12px",
        width: "300px",
        position: "relative",
        borderRadius: "8px",
        boxShadow: "0 2px 8px rgba(0,0,0,0.1)",
        backgroundColor: "#fff",
      }}
    >
      <h2>
        {city}, {data.sys.country}
      </h2>
      <p style={{ textTransform: "capitalize" }}>{data.weather[0].description}</p>
      <p>Temp: {Math.round(data.main.temp)}¬∞C</p>
      <p>Humidity: {data.main.humidity}%</p>
      <p>Wind: {data.wind.speed} km/h</p>
      <p><strong>Time of Day:</strong> {getTimeOfDay()}</p>

      <button
        onClick={toggleFavorite}
        style={{
          position: "absolute",
          top: "10px",
          right: "10px",
          background: "none",
          border: "none",
          cursor: "pointer",
          fontSize: "24px",
          color: isFavorite ? "red" : "grey",
          userSelect: "none",
        }}
        aria-label={isFavorite ? "Remove from favorites" : "Add to favorites"}
      >
        {isFavorite ? "‚ù§Ô∏è" : "ü§ç"} {getEmoji()}
      </button>
    </div>
  );
}

export default WeatherCard;
